import React, { Component } from 'react';
import './App.css';

import Bargraph from './components/Bargraph/Bargraph.js';
import Currencyselector from './components/Currencyselector/Currencyselector.js';
import Form from './components/Form/Form.js';

class App extends Component {
    state = {
        isLoading: false,
        apiData: [],
        results: [],
        rates: {}
    }

    barGraph = () => {
        const barGraph = this.state.barGraph;
        this.setState({
            barGraph,
        });
    }

    currencySelector = () => {
        const currencySelector = this.state.barGraph;
        this.setState({
            currencySelector,
        });
    }

    form = () => {
        const form = this.state.form;
        this.setState({
            form,
        });
    }


    componentDidMount() {
        fetch('https://api.exchangeratesapi.io/latest')
            .then(response => response.json())
            .then(data => {
                console.log(data)

                this.setState({
                    rates: data.rates,
                    results: []
                });
            })
    }


    rateKeys() {
        return Object.keys(this.state.rates);
    }


    render() {
        return (
            <div className="graph">
                {
                    this.rateKeys().map(countryCode => (
                        <div class="graph" style={{ height: this.state.rates[countryCode] + "%" }}>
                            {countryCode}{this.state.rates[countryCode]}
                        </div>
                    ))
                }
            </div>

        );
    }
}

export default App;
